sourcesContent:
import Post from "../models/post";

export const createPost = async (req, res) => {
    try {
        console.log(req.user);
        const { title, content, tags, image } = req.body;
        
        // Get userId from isAuthenticated middleware
        const author = req.user.userId; // Assuming the token contains the user's id in the payload
        console.log(author);
        if (!title || !content) {
            return res.status(400).json({ message: "Title and content are required." });
        }
        if (!author) {
            return res.status(400).json({ error: 'Author information is missing' });
        }

        const newPost = new Post({
            title,
            content,
            tags,
            image, // Include the image
            author,
        });
        const savedPost = await newPost.save();

        res.status(201).json(savedPost);
        // res.status(201).json({ message: "Post created successfully", savedPost });
    } catch (error) {
        console.error(error);
        res.status(500).json({ message: "Failed to create post.", error: error.message });
    }
};

// Update a post
export const updatePost = async (req, res) => {
    try {
        const { postId } = req.params;
        const { title, content, tags, image } = req.body;
        const userId = req.user.userId;

        const post = await Post.findById(postId);

        if (!post) {
            return res.status(404).json({ message: "Post not found." });
        }

        if (post.author.toString() !== userId) {
            return res.status(403).json({ message: "You are not authorized to update this post." });
        }

        // Update the post
        post.title = title || post.title;
        post.content = content || post.content;
        post.tags = tags || post.tags;
        post.image = image || post.image;

        const updatedPost = await post.save();
        res.status(200).json(updatedPost);
        // res.status(200).json({ message: "Post updated successfully", updatedPost });
    } catch (error) {
        console.error(error);
        res.status(500).json({ message: "Failed to update post.", error: error.message });
    }
};

// Delete a post
export const deletePost = async (req, res) => {
    try {
        const { postId } = req.params;
        const userId = req.user.userId;

        const post = await Post.findById(postId);
        console.log('hey this is post = ' + post);
        

        if (!post) {
            return res.status(404).json({ message: "Post not found." });
        }

        if (post.author.toString() !== userId) {
            return res.status(403).json({ message: "You are not authorized to delete this post." });
        }

        await post.deleteOne({ _id: postId });
        res.status(200).json({ message: "Post deleted successfully." });
    } catch (error) {
        console.error(error);
        res.status(500).json({ message: "Failed to delete post.", error: error.message });
    }
};

